        instrument_goal(P1, N1, O1, Q1)
    ;   Q1=P1
    ),
    actual_cut(F, G, P1),
    R1=(P:-R, Y, Q1, G1, H1, B1),
    add_location(R1, L, S1),
    I=[S1|J].
actual_cut(A, B, C) :-
