head_arg_matches(A, B, C, D, E) :-
    head_arg_matches(A, B, C, D, E, [], _).
head_arg_matches(A, B, C, D, E, F, G) :-
    head_arg_matches_(A, B, C, F, H, E, G),
    list2conj(H, D).
head_arg_matches_([], [], A, B, [], A, B).
head_arg_matches_([silent(A-_)|B], [C|D], E, F, G, H, I) :-
    !,
    (   C==(+)
    ->  term_variables(A, J, F),
        head_arg_matches_(B,
                          D,
                          E,
                          J,
                          G,
                          H,
                          I)
    ;   head_arg_matches_(B,
                          D,
                          E,
                          F,
                          G,
                          H,
                          I)
    ).
head_arg_matches_([A-B|C], [D|E], F, G, H, I, J) :-
    (   var(A)
    ->  (   lookup_eq(F, A, K)
        ->  (   D=(+)
            ->  (   memberchk_eq(A, G)
                ->  H=[B=K|L],
                    M=G
                ;   H=[B==K|L],
                    M=[A|G]
                )
            ;   H=[B==K|L],
                M=G
            ),
            N=F
        ;   N=[A-B|F],
            H=L,
            (   D=(+)
            ->  M=[A|G]
            ;   M=G
            )
        ),
        O=C,
        P=E
    ;   ground(A),
        A='$chr_identifier_match'(Q, R)
    ->  identifier_label_atom(R, B, Q, S),
        H=[S|L],
        F=N,
        M=G,
        O=C,
        P=E
    ;   atomic(A)
    ->  (   D=(+)
        ->  H=[B=A|L]
        ;   H=[B==A|L]
        ),
        F=N,
        M=G,
        O=C,
        P=E
    ;   D==(+),
        is_ground(G, A)
    ->  copy_with_variable_replacement(A, T, F),
        H=[B=T|L],
        F=N,
        M=G,
        O=C,
        P=E
    ;   D==(?),
        is_ground(G, A)
    ->  copy_with_variable_replacement(A, T, F),
        H=[B==T|L],
        F=N,
        M=G,
        O=C,
        P=E
    ;   A=..[_|U],
        functor(A, V, W),
        functor(X, V, W),
        X=..[_|Y],
        (   D=(+)
        ->  H=[B=X|L]
        ;   H=[nonvar(B), B=X|L]
        ),
        pairup(U, Y, Z),
        append(Z, C, O),
        replicate(W, D, A1),
        append(A1, E, P),
        N=F,
        M=G
    ),
    head_arg_matches_(O,
                      P,
                      N,
                      M,
                      L,
                      I,
                      J).
add_heads_types([], A, A).
