    ).
rest_heads_retrieval_and_matching_n([], _, _, _, _, [], [], A, A, _, B, B).
rest_heads_retrieval_and_matching_n([A|B], [_|C], D, E, F, [G|H], [I|J], K, L, _, M, N) :-
    functor(A, O, P),
    head_info(A, P, Q, _, _, R),
    get_store_type(O/P, S),
    (   S==default
    ->  passive_head_via(A, [F|D], K, T, U),
