    head_info(A, G, Q, R, S, T),
    get_constraint_mode(F/G, U),
    head_arg_matches(T, U, [], V, W, [], X),
    build_head(F, G, H, S, Y),
    append(C, N, Z),
    append(D, L, A1),
    reorder_heads(M, A, Z, A1, B1, C1),
