    once((   G=[J],
             H==[]
         ;   H=[J],
             G==[],
             \+ may_trigger(A)
         )),
    all_distinct_var_args(J).
partial_wake_args([], _, _, _).
partial_wake_args([A|B], [C|D], E, F) :-
