    ).
generate_show_rules([], A, A).
generate_show_rules([A/B|C], [D|E], F) :-
    functor(G, A, B),
    inc_rule_count(H),
    D=pragma(rule([], ['$show', G], true, writeln(G)), ids([], [0, 1]), [passive(1)], no, H),
