  copy_term(GridIn,Grid1),
  grid_detect_bg(Grid1,Background),
  maplist(to_grid_bg(CT,Grid1),Background),
  get_bg_label(BGL),
  get_bgc(BG),subst001(Grid1,BG,BGL,GridO),!.
grid_label_bg(CT,GridIn,GridO):- CT=s,!,
  copy_term(GridIn,Grid1),
  grid_detect_bg(Grid1,Background),
  maplist(to_grid_bg(CT,Grid1),Background),
  get_bg_label(BGL),
