constrain_grid(CT,Trig,Grid1,GridO):- 
  fpad_grid(CT,Grid1,Grid2),
  grid_label_bg(CT,Grid2,Grid3),
  must_det_ll(constrain_grid_now(CT,Trig,Grid3,GridO)),!.
  


