count_o_neighbors(C,H,V,Dirs,GridIn):- 
  muarc_mod(M),
  findall(Dir,
    (M:is_adjacent_hv(H,V,Dir,H2,V2),
     get_color_at(H2,V2,GridIn,C1O),
       is_spec_fg_color(C1O,_),C1O\=@=C), 
    Dirs).

count_c_neighbors(C,H,V,Dirs,GridIn):- 
  muarc_mod(M),
  findall(Dir,
    (M:is_adjacent_hv(H,V,Dir,H2,V2),
     get_color_at(H2,V2,GridIn,C1O),
